mapper.update:
update dt_pairing
set dt_id = #{dtId,jdbcType=INTEGER},
  a_litemid = #{aLitemid,jdbcType=INTEGER},
  b_litemid = #{bLitemid,jdbcType=INTEGER}
where dtd_id = #{dtdId,jdbcType=INTEGER}

mapper.select:
select dtd_id, dt_id, a_litemid, b_litemid
from dt_pairing
where dtd_id = #{dtdId,jdbcType=INTEGER}
